    floodFill(startX, startY,colData){
        let points = [{x:startX, y:startY}],
            point,
            seen = {},
            steps = [[1, 0], [0, 1], [0, -1], [-1, 0]],
            ways = steps.length,
            imgHSV,
            key,
            x,
            y,
            offset,
            i,
            x2,
            y2;

        // Keep going while we have points to walk
        while(!!(point = points.pop())) {
            x = point.x;
            y = point.y;
            offset = this.getPointOffset(x, y);
            imgHSV = this.RGBtoHSV(this.imgLayer.data[offset], this.imgLayer.data[offset+1], this.imgLayer.data[offset+2]);

            // Move to next point if this pixel isn't within tolerance of the colour being filled
            if(!this.tolerance_equal(offset, colData)) {
                continue;
            }

            // Update the pixel to the fill colour and add neighbours onto stack to traverse 
            // the fill area
            i = ways;
            while(i--) {
                // Use the same loop for setting RGBA as for checking the neighbouring pixels
                if(i < 1) {
                    let color = this.HSVtoRGB(this.color.asHSV.h, this.color.asHSV.s, (imgHSV.v-colData.vMean)*(1-(colData.vMean-.5)/.5)+this.color.asHSV.v );
                    this.colorLayer.data[offset] = color.r;
                    this.colorLayer.data[offset+1] = color.g;
                    this.colorLayer.data[offset+2] = color.b;
                    this.colorLayer.data[offset+3] = 255;
                }

                // Get the new coordinate by adjusting x and y based on current step
                x2 = x + steps[i][0];
                y2 = y + steps[i][1];
                key = x2 + ',' + y2;

                // If new coordinate is out of bounds, or we've already added it, then skip to 
                // trying the next neighbour without adding this one
                let newOffset = this.getPointOffset(x2,y2);
                if(x2 < 0 || y2 < 0 || x2 >= this.canvasWidth || y2 >= this.canvasHeight ||  seen[key] /*|| this.colorLayer.data[newOffset+3]>0*/ || this.cannyLayer.data[newOffset]>0) {
                    continue;
                }

                // Push neighbour onto points array to be processed, and tag as seen
                points.push({ x: x2, y: y2 });
                seen[key] = true;
            }
        }
    }
    getPointOffset(x,y){
        return (x*this.canvasWidth+y)*4;
    }
    tolerance_equal(offset, data){
        let pixcol = this.RGBtoHSV(this.imgLayer.data[offset], this.imgLayer.data[offset+1], this.imgLayer.data[offset+2]);
        return Math.abs(pixcol.h-data.hMean) < data.hDev*(this.minHDev*3.0/data.hDev) && Math.abs(pixcol.s-data.sMean) < data.sDev*(this.minSDev*3.0/data.sDev);
        //return Math.abs(pixcol.h-data.hMean) < this.hThresh && Math.abs(pixcol.s-data.sMean) < this.sThresh;
    }
    
    HEXtoRGB(hex){
        hex = hex.charAt(0) == '#'? hex.substr(1): hex;
        let out = {
            r: 0,
            g: 0,
            b: 0
        }
        if(hex.length>3){
            out.r = parseInt(hex.substr(0,2), 16);
            out.g = parseInt(hex.substr(2,2), 16);
            out.b= parseInt(hex.substr(4,2), 16);
        } else {
            out.r = parseInt(hex.substr(0,1), 16);
            out.g = parseInt(hex.substr(1,1), 16);
            out.b = parseInt(hex.substr(2,1), 16);
        }
        return out;
    }
    RGBtoHEX(r,g?,b?){
        if(typeof r == 'object'){
            return '#'+r.r.toString(16)+r.g.toString(16)+r.b.toString(16);
        }
        return '#'+r.toString(16)+g.toString(16)+b.toString(16);
    }
    RGBtoHSV(r,g?,b?){
        let input = {
            r: 0,
            g: 0,
            b: 0
        };
        if(typeof r == "object"){
            input.r = r.r;
            input.b = r.b;
            input.g = r.g;
        } else {
            input.r = r
            input.g = g
            input.b = b
        }
        /**
     * Converts an RGB color value to HSV. Conversion formula
     * adapted from http://en.wikipedia.org/wiki/HSV_color_space.
     * Assumes r, g, and b are contained in the set [0, 255] and
     * returns h, s, and v in the set [0, 1].
     *
     * @param   Object  r       RGB Representation
     *
     * @param   Number  r       The red color value
     * @param   Number  g       The green color value
     * @param   Number  b       The blue color value
     * @return  Object          The HSV representation
     */
        input.r = input.r/255, input.g = input.g/255, input.b = input.b/255;
        var max = Math.max(input.r, input.g, input.b), min = Math.min(input.r, input.g, input.b);
        var h, s, v = max;

        var d = max - min;
        s = max == 0 ? 0 : d / max;

        if(max == min){
            h = 0; // achromatic
        }else{
            switch(max){
                case input.r: h = (input.g - input.b) / d + (input.g < input.b ? 6 : 0); break;
                case input.g: h = (input.b - input.r) / d + 2; break;
                case input.b: h = (input.r - input.g) / d + 4; break;
            }
            h /= 6;
        }

        return {h:h,s:s,v:v};

        
      
    }
    HSVtoRGB(h,s?,v?){
        let input = {
            h: 0,
            s: 0,
            v: 0
        };
        if(typeof h == "object"){
            input.h = h.h;
            input.v = h.v;
            input.s = h.s;
        } else {
            input.h = h
            input.s = s
            input.v = v
        }
        /**
     * Converts an HSV color value to RGB. Conversion formula
     * adapted from http://en.wikipedia.org/wiki/HSV_color_space.
     * Assumes h, s, and v are contained in the set [0, 1] and
     * returns r, g, and b in the set [0, 255].
     *
     * @param   Object  h       HSV Representation
     *
     * @param   Number  h       The hue
     * @param   Number  s       The saturation
     * @param   Number  v       The value
     * @return  Object          The RGB representation
     */

        var r, g, b;

        var i = Math.floor(input.h * 6);
        var f = input.h * 6 - i;
        var p = input.v * (1 - input.s);
        var q = input.v * (1 - f * input.s);
        var t = input.v * (1 - (1 - f) * input.s);

        switch(i % 6){
            case 0: r = input.v, g = t, b = p; break;
            case 1: r = q, g = input.v, b = p; break;
            case 2: r = p, g = input.v, b = t; break;
            case 3: r = p, g = q, b = input.v; break;
            case 4: r = t, g = p, b = input.v; break;
            case 5: r = input.v, g = p, b = q; break;
        }

        return {r:r * 255, g:g * 255, b:b * 255};
    }

